import React, { useContext, useState } from 'react'
import restaurantsFinder from '../apis/restaurantsFinder'
import RestaurantsContext from '../context/restaurantsContext'

const AddRestaurant = () => {
    const { addRestaurant } = useContext(RestaurantsContext)
    const [name, setName] = useState('')
    const [location, setLocation] = useState('')
    const [priceRange, setPriceRange] = useState('Price Range')

    const handleSubmit = async (e) => {
        e.preventDefault()
        try {
            const response = await restaurantsFinder.post('/', {
                name,
                location,
                price_range: priceRange
            })
            addRestaurant(response.data.data.restaurant)
            setName('')
            setLocation('')
            setPriceRange('Price Range')
        } catch (err) {
            console.log(err)
        }
    }

    return (
        <div className='mb-4'>
            <form action=''>
                <div className='form-row d-flex gap-5'>
                    <div className='col'>
                        <input value={name} type='text' className='form-control' placeholder='name' onChange={(e) => setName(e.target.value)} />
                    </div>

                    <div className='col'>
                        <input value={location} onChange={(e) => setLocation(e.target.value)} type='text' className='form-control' placeholder='location' />
                    </div>

                    <div className='col'>
                        <select value={priceRange} onChange={e => setPriceRange(e.target.value)} className='custom-select my-1 mr-sm-2 p-2' placeholder='price range'>
                            <option disabled>Price Range</option>
                            <option value='1'>$</option>
                            <option value='2'>$$</option>
                            <option value='3'>$$$</option>
                            <option value='4'>$$$$</option>
                            <option value='5'>$$$$$</option>
                        </select>
                    </div>
                    <button onClick={(e) => handleSubmit(e)} className='btn btn-primary'>Add</button>

                </div>

            </form>

        </div>
    )
}

export default AddRestaurant
