import React, { useContext, useEffect } from 'react'
import { useNavigate } from 'react-router-dom'
import restaurantsFinder from '../apis/restaurantsFinder'
import RestaurantContext from '../context/restaurantsContext'

const ContactsList = (props) => {

    const navigate = useNavigate()
    const { restaurants, setRestaurants } = useContext(RestaurantContext);

    useEffect(() => {

        const fetchData = async () => {
            try {
                const response = await restaurantsFinder.get('/')
                setRestaurants(response.data.data.restaurants)
            } catch (err) {
                console.log(err)
            }
        }
        fetchData()
        // eslint-disable-next-line
    }, [])

    const handleDelete = async (e, id) => {
        e.stopPropagation()
        try {
            const response = await restaurantsFinder.delete(`/${id}`)
            setRestaurants(restaurants.filter(x => x.id !== id))
        } catch (err) {
            console.log(err)
        }
    }

    const handleUpdate = async (e, id) => {
        e.stopPropagation()
        navigate(`/restaurants/${id}/update`, id)
    }

    const handleRestaurantSelect = (id) => {
        navigate(`/restaurants/${id}/`)

    }

    return (
        <div className='list-group'>
            <table className='table table-hover'>
                <thead>
                    <tr className='bg-primary'>
                        <th scope='col'>Serial Number</th>
                        <th scope='col'>Name</th>
                        <th scope='col'>Phone Number</th>
                        <th scope='col'>Email</th>
                        <th scope='col'>Update</th>
                        <th scope='col'>Delete</th>
                    </tr>
                </thead>
                <tbody>
                    {restaurants.length > 0 && restaurants.map((restaurant, index) => {
                        return (
                            <tr key={index} onClick={(id) => handleRestaurantSelect(restaurant.id)}>
                                <td>{index + 1}</td>
                                <td>{restaurant.name}</td>
                                <td>{restaurant.location}</td>
                                <td>{'$'.repeat(restaurant.price_range)}</td>
                                <td>Ratings</td>
                                <td><button onClick={(e) => handleUpdate(e, restaurant.id)} className='btn btn-warning'>Update</button></td>
                                <td><button onClick={(e) => handleDelete(e, restaurant.id)} className='btn btn-danger'>Delete</button></td>
                            </tr>

                        )
                    })
                    }
                </tbody>
            </table>
        </div>
    )
}

export default ContactsList
